# ---------- Linear algebra helper: solve V x = b via Cholesky ----------
chol_solve <- function(L, b) {
  # Given V = L %*% t(L) with L lower-triangular (from chol(..., pivot=FALSE)),
  # solve V x = b
  y <- forwardsolve(L, b, upper.tri = FALSE, transpose = FALSE)
  x <- backsolve(t(L), y, upper.tri = TRUE, transpose = FALSE)
  x
}

# ---------- For each matrix component ---------------------------------------------
kernel <- function(xi, xj, type = c("rbf", "linear", "quad", "rbf_linear"),
                   theta1 = 1, theta2 = 0.4) {
  type <- match.arg(type)
  switch(type,
         rbf = {
           d2 <- sum((xi - xj)^2)
           return(theta1 * exp(-0.5*d2 / theta2))
         },
         
         linear = {
           return(sum(xi*xj) + 1)
         },
         
         quad = {
           return((sum(xi*xj) + 1)^2)
         },
         rbf_linear = {
           d2 <- sum((xi - xj)^2)
           return(theta1 * exp(-0.5*d2 / theta2) + sum(xi*xj) + 1)
         }
  )
}


# ---------- Matrix build ---------------------------------------------
kernel_matrix <- function(X1, X2 = X1,
                          type = "rbf",
                          theta1 = 1,
                          theta2 = 1,
                          noise = 0,
                          jitter = 1e-5) {
  X1 <- as.matrix(X1)
  X2 <- as.matrix(X2)
  N1 <- nrow(X1)
  N2 <- nrow(X2)
  K <- matrix(0, nrow = N1, ncol = N2)
  
  for (i in 1:N1) {
    xi <- X1[i, , drop = FALSE]
    for (j in 1:N2) {
      xj <- X2[j, , drop = FALSE]
      K[i, j] <- kernel(xi, xj,
                        type = type,
                        theta1 = theta1,
                        theta2 = theta2)
    }
  }
  # noise for only diagonal component
  if (noise != 0 && identical(X1, X2)) {
    K <- K + noise * diag(N1)
  }
  return(K)
}

#GPに算
GP_pred <- function(X_train, Y_train, X_pred, 
                    theta1 = 1, theta2 = 1,
                    theta3 = 0, #noise
                    type = c("rbf", "linear", "quad", "rbf_linear")
                    ){
                    #check input
                    X_train <- as.matrix(X_train)
                    Y_train <- as.matrix(Y_train)
                    X_pred <- as.matrix(X_pred)
                    N <- nrow(X_train)
                    M <- nrow(X_pred)
                    if (N != length(Y_train)){
                      stop("length(x_train) must equal length(y_train)")
                    }
                    # learning K(N×N) with noise
                    K <- kernel_matrix(X_train, X_train,
                                       type = type,
                                       theta1 = theta1, theta2 = theta2,
                                       noise = theta3)
                    
                    # prediction k(N×M): without noise
                    k <- kernel_matrix(X_train, X_pred,
                                       type = type,
                                       theta1 = theta1, theta2 = theta2)
                    
                    # prediction s(M×M)
                    s <- kernel_matrix(X_pred, X_pred,
                                       type = type,
                                       theta1 = theta1, theta2 = theta2)
                    
                    # Cholesky: V = L L^T
                    L <- chol(K)  # upper-tri by default in R: chol(V) returns U s.t. t(U)U = V
                    # We'll convert to lower-tri for forwardsolve/backsolve style:
                    # If U is upper, then V = t(U)U => let L = t(U)
                    L <- t(L)
                    
                    yy <- chol_solve(L, Y_train)#K*yy = y_train, solve yy
                    v  <- chol_solve(L, k)#Kv=k, solve v
                    mu <- t(k)%*%yy#mean
                    var_f <- s - t(k) %*% v #fuction variance var_f(M×M)
                    if (abs(theta3) > .Machine$double.eps^0.5) {
                      var_y <- var_f + theta3 * diag(M) #observation variance var_y
                      return(list(mu = mu, var_f = var_f, var_y = var_y))
                    } else {
                      return(list(mu = mu, var_f = var_f))
                    }
}

# ============================================================
# UCB Optimization utilities
# ============================================================

# ============================================================
# Threshold-aware acquisition
# purpose: ">2", ">=2", "<5", "<=5"
# score: (mean shift from threshold) + kappa * sd
#   - for ">" : (mu - thr) + kappa*sd
#   - for "<" : (thr - mu) + kappa*sd
# Notes:
# - This keeps the same "UCB style" while respecting the threshold.
# ============================================================
ucb_score <- function(mu, sd, purpose, kappa = 2.0, parse_fun = parse_purpose) {
  
  pp <- parse_fun(purpose)
  
  mu <- as.numeric(mu)
  sd <- as.numeric(sd)
  sd <- pmax(sd, 1e-12)  # avoid zero
  
  # default: maximize mu
  if (pp$type != "bound") {
    return(mu + kappa * sd)
  }
  
  thr <- if(is.na(pp$value)) 0 else pp$value
  
  if (pp$op %in% c(">", ">=")) {
    # maximize "margin above threshold" with uncertainty bonus
    return((mu - thr) + kappa * sd)
  }
  
  if (pp$op %in% c("<", "<=")) {
    # minimize mu => maximize "margin below threshold"
    return((thr - mu) + kappa * sd)
  }
  
  # fallback
  mu + kappa * sd
}

standardize_fit <- function(X, Z, y,
                            standardize_X = TRUE,
                            intercept_col = 1L) {
  X <- as.matrix(X)
  Z <- as.matrix(Z)
  y <- as.numeric(y)
  
  n <- length(y)
  p <- ncol(X)
  d <- ncol(Z)
  
  # --- y center/scale ---
  y_mean <- mean(y)
  y_sd   <- sd(y)
  if (y_sd == 0) y_sd <- 1
  y_s <- (y - y_mean) / y_sd
  
  # --- Z standardize ---
  Z_mean <- colMeans(Z)
  Z_sd <- apply(Z, 2, sd)
  Z_sd[Z_sd == 0] <- 1
  Z_s <- sweep(sweep(Z, 2, Z_mean, "-"), 2, Z_sd, "/")
  
  # --- X standardize (optional) ---
  X_s <- X
  X_mean <- rep(0, p)
  X_sd   <- rep(1, p)
  
  if (standardize_X) {
    for (j in seq_len(p)) {
      if (!is.null(intercept_col) && j == intercept_col) {
        # intercept column left unchanged
        X_mean[j] <- 0
        X_sd[j] <- 1
      } else {
        X_mean[j] <- mean(X[, j])
        X_sd[j]   <- sd(X[, j])
        if (X_sd[j] == 0) X_sd[j] <- 1
        X_s[, j] <- (X[, j] - X_mean[j]) / X_sd[j]
      }
    }
  }
  
  list(
    X = X_s, Z = Z_s, y = y_s,
    y_mean = y_mean, y_sd = y_sd,
    Z_mean = Z_mean, Z_sd = Z_sd,
    X_mean = X_mean, X_sd = X_sd,
    standardize_X = standardize_X,
    intercept_col = intercept_col
  )
}

standardize_apply <- function(Xnew, Znew, std) {
  Xnew <- as.matrix(Xnew)
  Znew <- as.matrix(Znew)
  
  # apply Z transform
  Z_s <- sweep(sweep(Znew, 2, std$Z_mean, "-"), 2, std$Z_sd, "/")
  
  # apply X transform (if used)
  X_s <- Xnew
  if (isTRUE(std$standardize_X)) {
    p <- ncol(Xnew)
    for (j in seq_len(p)) {
      if (!is.null(std$intercept_col) && j == std$intercept_col) {
        # intercept unchanged
      } else {
        X_s[, j] <- (Xnew[, j] - std$X_mean[j]) / std$X_sd[j]
      }
    }
  }
  
  list(X = X_s, Z = Z_s)
}

# For prediction mean back-transform:
# if y was standardized: y = y_mean + y_sd * y_scaled
destandardize_y <- function(y_scaled, std) {
  std$y_mean + std$y_sd * y_scaled
}

destandardize_y_sd <- function(sd_scaled, std) {
  std$y_sd * sd_scaled
}



# --------------- box-cox -------------------------------
boxcox_transform <- function(y, lambda) {
  if (any(y <= 0)) stop("Box-Cox requires y > 0")
  if (abs(lambda) < 1e-8) {
    log(y)
  } else {
    (y^lambda - 1) / lambda
  }
}
boxcox_inverse <- function(z, lambda) {
  if (abs(lambda) < 1e-8) {
    exp(z)
  } else {
    pmax(lambda * z + 1, 0)^(1 / lambda)
  }
}

# ------ function to detect boxcox usage ----------------------
use_boxcox <- function(lambda) is.numeric(lambda) && length(lambda) == 1L && is.finite(lambda)

# Identity-safe inverse (Box–Cox OFF => identity)
inv_y <- function(z, lambda = NULL) {
  if (use_boxcox(lambda)) boxcox_inverse(z, lambda) else z
}

# Optional forward transform (Box–Cox OFF => identity)
fwd_y <- function(y, lambda = NULL) {
  if (use_boxcox(lambda)) boxcox_transform(y, lambda) else y
}

